@js:
sort=[];
push=(title,url,type)=> sort.push({
		title: title,
		url: url,
		style: {
				layout_flexGrow: 1,
				layout_flexBasisPercent: type
			}
	});
$$=(a,b,c)=>`/shuku/{{page}\}_0_${a}_${b}_0_${c}_0.html`;
["综合全部","言情小说","穿越重生","现代都市","灵异小说","历史军事","奇幻小说","玄幻小说","虚拟网游","科幻小说"].map((title,c)=>{
		if(c)c+=3;
		push(title,$$(0,0,c),1);
		["默认","时间","字数","热门"].map((title,b)=>{
				["["+title+"]","连载","完结"].map((title,a)=>{
						push(title,$$(a,b,c),0.25);
					});
			});
	});
JSON.stringify(sort);

