@js:
url="https:"+result.match(/initTxt\("([^"]+)/)[1]
eval("$="+String(java.ajax(url)).match(/_txt_call\(([\s\S]+\})\)/)[1])
reg=$.replace
txt=$.content

if(txt!=null)
		for(key in reg)txt=txt.replace(new RegExp(reg[key],"ig"),key);

else
		txt="章节加载失败/内容正在手打中，请稍后访问~";

txt

