    var cX = aQ(c8, false);
    var cT = cS['length'] - 0x1;
    var cP = cS[cT - 0x1],
    cQ = cS[0x0],
    cR = 0x9e3779b9;
    var cL, cM, cN = Math['floor'](0x6+(0x34/(cT+0x1))),
