ruleToc.chapterList:
@js:
let u = baseUrl.replace('list','content')

let v = [];
let list = [];
JSON.parse(result).data.list.map($=>{

//分卷判定
		V = $.volumeTitle
		if(v[v.length-1]!=V&&!/^\s*$/.test(V)){
				v.push(V)
				list.push({
						name: '📖['+V+']📖',
						volume: true
					})
			}

		return list.push({
				name: $.chapterName,
				url: u+'?chapterId='+$.chapterId+'&volumeId=1',
				info: '更新时间：'+java.timeFormatUTC($.updateTime*1000,'yyyy-MM-dd HH:mm',12)
			});
	});
v.length<2?list.filter($=>!$.volume):list

